package introduction;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/* Byte
		 * Min:-128
		 * MaX: 127
		 * Dafault:0
		 */
		byte byteVariable=100; 
		System.out.println("Byte Value "+ byteVariable);
		
		short shortVariable=100;
		System.out.println("Short Value"+ shortVariable);
		
		int integerVariable=100000;
		System.out.println("Int Value"+ integerVariable);
		
		long longVariable=100000000000L;
		System.out.println("Long Value"+ longVariable);
		
		float floatVariable= 3.14F;
		System.out.println("Float Variable"+ floatVariable);

		double doubleVariable=63.33;
		
		System.out.println("Double Variable:"+ doubleVariable);
		
		boolean booleanVariable= false; 
		System.out.println("Boolean Variable:" + booleanVariable);
		
		char charVariable='C';
		System.out.println("Character Value:"+ charVariable);
	}

}
